	public static String queryVisaCountry(String search){
		if(search == null){
			return null;
		}
		JSONArray json = new JSONArray();
		for (int i = 0 ; i < jsonArray.size(); i++) {
			JSONObject object = (JSONObject) jsonArray.get(i);
			if (object.getString("text").indexOf(search) != -1) {
				json.add(object);
			}
		}
		if(json.size()>0){
			JSONObject resultObject = (JSONObject) json.get(0);
			return resultObject.getString("text");
		}else{
			return null;
		}
		
	}
	
	
	public static void main(String[] args) {
			System.out.println(VisaUtil.queryVisaCountry(null));
	}
